import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Navbar from '../components/Navbar'; // Assuming Navbar can be used for admin section
import { useNavigate } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';
import { toast } from 'react-toastify';

const AdminCallbackList = () => {
  const [callbackRequests, setCallbackRequests] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [viewedCallback, setViewedCallback] = useState(null);
  const navigate = useNavigate();

  const fetchCallbackRequests = async () => {
    const token = localStorage.getItem('token');
    const user = localStorage.getItem('user');

    if (!token || !user) {
      navigate('/admin/login');
      return;
    }

    try {
      const userData = JSON.parse(user);
      if (userData.role !== 'admin') {
        navigate('/login');
        return;
      }

      const config = {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      };
      const response = await axios.get('http://localhost:5000/api/callbacks/all', config);
      setCallbackRequests(response.data.requests);
      setLoading(false);
    } catch (err) {
      console.error('Error fetching callback requests:', err);
      if (err.response && (err.response.status === 401 || err.response.status === 403)) {
        setError('You are not authorized to view this page. Please log in as an administrator.');
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        navigate('/admin/login');
      } else {
        setError('Failed to load callback requests. Server error.');
      }
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchCallbackRequests();
  }, [navigate]);

  const handleViewCallback = async (callback) => {
    try {
      const token = localStorage.getItem('token');
      const config = {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      };
      
      const response = await axios.patch(`http://localhost:5000/api/callbacks/${callback._id}/view`, {}, config);
      
      // Update the callback in the list with the new viewed status
      setCallbackRequests(prevRequests => 
        prevRequests.map(req => 
          req._id === callback._id 
            ? { ...req, isViewed: true, viewedAt: new Date() }
            : req
        )
      );
      
      setViewedCallback(response.data.callback);
      toast.success('Callback marked as viewed. It will be deleted in 24 hours.');
    } catch (error) {
      console.error('Error marking callback as viewed:', error);
      toast.error('Failed to mark callback as viewed');
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-black text-white flex items-center justify-center">
        <p className="text-xl">Loading callback requests...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-black text-red-400 flex items-center justify-center">
        <p className="text-xl">Error: {error}</p>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-black text-white">
      <Navbar /> {/* You might want a separate Admin Navbar for consistency */}
      <div className="container mx-auto py-16 px-4 md:px-8">
        <h1 className="text-4xl font-bold mb-8 text-teal-400">Admin: Callback Requests</h1>
        
        {callbackRequests.length === 0 ? (
          <p className="text-gray-400">No callback requests found.</p>
        ) : (
          <div className="overflow-x-auto bg-gray-900 rounded-lg shadow-lg">
            <table className="min-w-full divide-y divide-gray-700">
              <thead className="bg-gray-700">
                <tr>
                  <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-300 uppercase tracking-wider">Name</th>
                  <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-300 uppercase tracking-wider">Phone</th>
                  <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-300 uppercase tracking-wider">Date & Time</th>
                  <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-300 uppercase tracking-wider">Enquiry For</th>
                  <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-300 uppercase tracking-wider">Status</th>
                  <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-300 uppercase tracking-wider">Actions</th>
                </tr>
              </thead>
              <tbody className="divide-y divide-gray-700">
                {callbackRequests.map((request) => (
                  <motion.tr
                    key={request._id}
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    exit={{ opacity: 0, y: -20 }}
                    className={`${request.isViewed ? 'bg-gray-800' : 'bg-gray-900'} transition-colors duration-300`}
                  >
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-200">
                      <motion.div 
                        className={`${request.isViewed ? 'blur-[3px]' : ''} transition-all duration-300`}
                        animate={{ filter: request.isViewed ? 'blur(3px)' : 'blur(0px)' }}
                      >
                        {request.name}
                      </motion.div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-200">
                      <motion.div 
                        className={`${request.isViewed ? 'blur-[3px]' : ''} transition-all duration-300`}
                        animate={{ filter: request.isViewed ? 'blur(3px)' : 'blur(0px)' }}
                      >
                        {request.phone}
                      </motion.div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-200">
                      <motion.div 
                        className={`${request.isViewed ? 'blur-[3px]' : ''} transition-all duration-300`}
                        animate={{ filter: request.isViewed ? 'blur(3px)' : 'blur(0px)' }}
                      >
                        {new Date(request.date).toLocaleString()}
                      </motion.div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-200">
                      <motion.div 
                        className={`${request.isViewed ? 'blur-[3px]' : ''} transition-all duration-300`}
                        animate={{ filter: request.isViewed ? 'blur(3px)' : 'blur(0px)' }}
                      >
                        {request.enquiryFor}
                      </motion.div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-200">
                      <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${
                        request.status === 'Pending' ? 'bg-yellow-100 text-yellow-800' : 
                        request.status === 'Contacted' ? 'bg-blue-100 text-blue-800' : 
                        'bg-green-100 text-green-800'
                      }`}>
                        {request.status}
                      </span>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-200">
                      {!request.isViewed && (
                        <motion.button
                          whileHover={{ scale: 1.05 }}
                          whileTap={{ scale: 0.95 }}
                          onClick={() => handleViewCallback(request)}
                          className="px-4 py-2 bg-teal-500 text-white rounded-md hover:bg-teal-600 transition-colors"
                        >
                          View
                        </motion.button>
                      )}
                    </td>
                  </motion.tr>
                ))}
              </tbody>
            </table>
          </div>
        )}

        <AnimatePresence>
          {viewedCallback && (
            <motion.div
              initial={{ opacity: 0, scale: 0.8 }}
              animate={{ opacity: 1, scale: 1 }}
              exit={{ opacity: 0, scale: 0.8 }}
              className="fixed inset-0 bg-black bg-opacity-75 flex items-center justify-center z-50"
              onClick={() => setViewedCallback(null)}
            >
              <motion.div
                className="bg-gray-800 p-8 rounded-lg shadow-xl max-w-md w-full"
                onClick={e => e.stopPropagation()}
              >
                <h2 className="text-2xl font-bold mb-4 text-teal-400">Callback Details</h2>
                <div className="space-y-4">
                  <p><span className="font-semibold">Name:</span> {viewedCallback.name}</p>
                  <p><span className="font-semibold">Phone:</span> {viewedCallback.phone}</p>
                  <p><span className="font-semibold">Date & Time:</span> {new Date(viewedCallback.date).toLocaleString()}</p>
                  <p><span className="font-semibold">Enquiry For:</span> {viewedCallback.enquiryFor}</p>
                  <p><span className="font-semibold">Status:</span> {viewedCallback.status}</p>
                </div>
                <motion.button
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                  onClick={() => setViewedCallback(null)}
                  className="mt-6 w-full bg-teal-500 text-white py-2 rounded-md hover:bg-teal-600 transition-colors"
                >
                  Close
                </motion.button>
              </motion.div>
            </motion.div>
          )}
        </AnimatePresence>
      </div>
    </div>
  );
};

export default AdminCallbackList;
