import React, { useState, useRef, useEffect } from 'react';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';
import viewImg from './view.jpg';

const WELCOME_TEXT = 'Welcome to Your Dashboard!';

const Dashboard = () => {
  const [isHovered, setIsHovered] = useState(false);
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const [bgVisible, setBgVisible] = useState(false);
  const [typedText, setTypedText] = useState('');
  const [showWidgets, setShowWidgets] = useState(false);
  const boxRef = useRef(null);

  // Fade-in background
  useEffect(() => {
    setTimeout(() => setBgVisible(true), 100);
    // Typewriter effect
    let i = 0;
    const typeInterval = setInterval(() => {
      setTypedText(WELCOME_TEXT.slice(0, i + 1));
      i++;
      if (i === WELCOME_TEXT.length) {
        clearInterval(typeInterval);
        setTimeout(() => setShowWidgets(true), 900); // Show widgets after text
      }
    }, 60);
    return () => clearInterval(typeInterval);
  }, []);

  const handleMouseMove = (e) => {
    if (boxRef.current) {
      const rect = boxRef.current.getBoundingClientRect();
      const x = e.clientX - rect.left;
      const y = e.clientY - rect.top;
      setMousePosition({ x, y });
    }
  };

  return (
    <div className="min-h-screen bg-black text-white relative overflow-hidden">
      {/* Blurred background image with fade-in */}
      <div
        className={`fixed inset-0 w-full h-full z-0 transition-opacity duration-1000 ${bgVisible ? 'opacity-100' : 'opacity-0'}`}
        style={{
          background: `url(${viewImg}) center/cover no-repeat`,
          filter: 'blur(12px) brightness(0.7)',
        }}
        aria-hidden="true"
      />
      <Navbar bgBlackOnlyOnDashboard />
      <div className="container mx-auto py-16 px-4 md:px-8 flex justify-center items-center min-h-[70vh] relative z-10">
        <div
          ref={boxRef}
          className={`relative rounded-xl shadow-2xl max-w-2xl w-full p-10 flex flex-col items-center justify-center bg-black/60 transition-all duration-300 border-2 ${
            isHovered
              ? 'border-blue-400 shadow-[0_0_24px_4px_rgba(56,189,248,0.5)]'
              : 'border-gray-700'
          }`}
          onMouseEnter={() => setIsHovered(true)}
          onMouseLeave={() => setIsHovered(false)}
          onMouseMove={handleMouseMove}
          style={{ minHeight: 400 }}
        >
          {/* Blue spotlight effect */}
          {isHovered && (
            <div
              className="pointer-events-none absolute z-10"
              style={{
                left: mousePosition.x,
                top: mousePosition.y,
                transform: 'translate(-50%, -50%)',
                width: 300,
                height: 300,
                background: 'radial-gradient(circle, rgba(56,189,248,0.25) 0%, rgba(56,189,248,0) 70%)',
                transition: 'all 0.1s ease-out',
                borderRadius: '50%',
              }}
            />
          )}
          {/* Welcome text with typewriter and glow */}
          <div className="relative z-20 text-center mb-8">
            <h1
              className="text-4xl md:text-5xl font-bold mb-4 drop-shadow-lg"
              style={{
                color: '#7dd3fc',
                textShadow: '0 0 16px #38bdf8, 0 0 32px #0ea5e9',
                letterSpacing: '0.04em',
                fontFamily: 'monospace',
                minHeight: 60,
                transition: 'text-shadow 0.5s',
              }}
            >
              {typedText}
              <span className="animate-pulse text-blue-400">{typedText.length < WELCOME_TEXT.length ? '|' : ''}</span>
            </h1>
          </div>
          {/* Animated widgets */}
          <div className="w-full flex flex-col md:flex-row gap-6 justify-center items-center">
            <Widget
              show={showWidgets}
              delay={0}
              side="left"
              title="Your Courses"
              accentColor="#38bdf8"
              content="See your enrolled courses and progress."
            />
            <Widget
              show={showWidgets}
              delay={200}
              side="right"
              title="Profile"
              accentColor="#0ea5e9"
              content="Manage your profile and settings."
            />
          </div>
        </div>
      </div>
      <Footer bgGray />
    </div>
  );
};

// Animated widget component
const Widget = ({ show, delay, side, title, accentColor, content }) => {
  const [visible, setVisible] = useState(false);
  useEffect(() => {
    if (show) {
      setTimeout(() => setVisible(true), delay);
    }
  }, [show, delay]);
  return (
    <div
      className={`w-full md:w-1/2 p-6 rounded-lg bg-black/70 border border-blue-500 shadow-lg transition-all duration-700 ${
        visible
          ? 'opacity-100 blur-0 translate-x-0'
          : `opacity-0 blur-md ${side === 'left' ? '-translate-x-16' : 'translate-x-16'}`
      }`}
      style={{ boxShadow: `0 0 24px 2px ${accentColor}33` }}
    >
      <h2 className="text-2xl font-semibold mb-2" style={{ color: accentColor }}>{title}</h2>
      <p className="text-gray-300">{content}</p>
    </div>
  );
};

export default Dashboard;
