import React, { useState, useRef } from 'react';
import Navbar from '../components/Navbar';

const Dashboard = () => {
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const [isHovered, setIsHovered] = useState(false);
  const boxRef = useRef(null);

  const handleMouseMove = (e) => {
    if (boxRef.current) {
      const rect = boxRef.current.getBoundingClientRect();
      const x = e.clientX - rect.left;
      const y = e.clientY - rect.top;
      setMousePosition({ x, y });
    }
  };

  return (
    <div className="min-h-screen bg-black text-white">
      <Navbar />
      <div className="container mx-auto py-16 px-4 md:px-8 flex justify-center items-center min-h-[70vh]">
        <div
          ref={boxRef}
          className="relative rounded-xl overflow-hidden shadow-2xl max-w-2xl w-full p-10 flex flex-col items-center justify-center"
          style={{
            background: `url('/images/view.jpg') center/cover no-repeat, #181818`,
            minHeight: '400px',
            border: '2px solid #22c55e',
          }}
          onMouseMove={handleMouseMove}
          onMouseEnter={() => setIsHovered(true)}
          onMouseLeave={() => setIsHovered(false)}
        >
          {/* Green glow effect */}
          {isHovered && (
            <div
              className="pointer-events-none absolute z-10"
              style={{
                left: mousePosition.x,
                top: mousePosition.y,
                transform: 'translate(-50%, -50%)',
                width: 300,
                height: 300,
                background: 'radial-gradient(circle, rgba(34,197,94,0.25) 0%, rgba(34,197,94,0) 70%)',
                transition: 'all 0.1s ease-out',
                borderRadius: '50%',
              }}
            />
          )}
          <div className="relative z-20 text-center">
            <h1 className="text-4xl font-bold mb-8 drop-shadow-lg">Welcome to your Dashboard!</h1>
            <p className="text-lg text-gray-300 mb-2">This is a placeholder for your user dashboard. You can add more content here.</p>
            <p className="text-gray-400 mt-4">Feel free to explore your courses, progress, and other features.</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
